const bcrypt = global.get('bcrypt');
const uuidv4 = global.get('uuid');

const formatted = moment(new Date()).format("YYYY-MM-DD HH:mm:ss");

async function gerarHashSenha(senha) {
  return await bcrypt.hash(senha, 10)
    .then(hash => hash)
    .catch(err => {
      throw err;
    });
}

